public class Part1 {
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int find = dna.indexOf(stopCodon, startIndex);
        while (find != -1){
            if ((find - startIndex) %3 == 0){
                return find;
            }
            find = dna.indexOf(stopCodon, find + 3);
        }
        return dna.length();
    }
    public void testFindStopCodon (){
        System.out.println("ATATTAATGGATATGGTATAAATG, stopcondo TAA" + findStopCodon("ATATTAATGGATATGGTTAAATG", 1, "TAA"));
    }
    public String findGene(String dna){
        int start = dna.indexOf("ATG");
        if (start == -1){
            return "";
        }
        int end1 = findStopCodon(dna, start + 3, "TAA");
        int end2 = findStopCodon(dna, start + 3, "TAG");
        int end3 = findStopCodon(dna, start + 3, "TGA");
        int end = Math.min(end1, Math.min(end2,end3));
        if (end == dna.length()){
            return "";
        }
        return dna.substring(start, end + 3);        
    }
    public void testFindGene(){
        // No ATG
        System.out.println("DNA: ATATTAAGGGATATTGTATAAAT, Gene: " + findGene("ATATTAAGGGATATTGTATAAAT"));
        // with ATG and one valid stop codon
        System.out.println("DNA: ATATTAATGGATATGGTATAAATG, Gene: " + findGene("ATATTAATGGATATGGTATAAATG"));
        // with ATG and two valid stop codons
        System.out.println("DNA: ATATTAATGGATATGGTATAGTAA, Gene: " + findGene("ATATTAATGGATATGGTATAGTAA"));
        // with ATG and no stop codons
        System.out.println("DNA: ATATTAATGGATATGGTATATATAT, Gene: " + findGene("ATATTAATGGATATGGTATATATAT"));
    }
    public void printAllGenes(String dna){
        int start_index = 0, count = 0;
        while (count <= 200){
            String dna_sub = dna.substring(start_index);
            String gene = findGene(dna_sub);
            if (gene == "") {break;}
            System.out.println(gene);
            count = count +1;
            start_index = dna.indexOf(gene, start_index) + gene.length();
        }           
    }
    public void testprintAllGenes(){
        printAllGenes("");
    }
}
